// Função para obter todos os produtos com filtros opcionais
export async function getProdutos({
  categoria,
  clube,
  cor,
  liga,
  ordenar = "recentes",
  precoMin,
  precoMax,
  temporada,
  edicao_especial,
  pesquisa,
  versao,
}: {
  categoria?: string
  clube?: string
  cor?: string
  liga?: string
  ordenar?: string
  precoMin?: string
  precoMax?: string
  temporada?: string
  edicao_especial?: boolean
  pesquisa?: string
  versao?: string
}) {
  // Criar chave de cache baseada nos filtros
  const cacheKey = CACHE_KEYS.PRODUTOS_FILTERED(
    JSON.stringify({ categoria, clube, cor, liga, ordenar, precoMin, precoMax, temporada, edicao_especial, pesquisa, versao })
  )
  
  // Verificar cache primeiro
  const cached = getCachedData<Product[]>(cacheKey)
  if (cached) {
    return cached
  }
  
  // Simular um atraso de rede
  await new Promise((resolve) => setTimeout(resolve, 50))

  let produtosFiltrados = [...produtos]

  // Filtrar por pesquisa de texto
  if (pesquisa && pesquisa.trim()) {
    const termoPesquisa = pesquisa.toLowerCase().trim()
    produtosFiltrados = produtosFiltrados.filter((produto) => {
      // Buscar em todos os campos relevantes
      const nomeMatch = produto.nome?.toLowerCase().includes(termoPesquisa) || false
      const descricaoMatch = produto.descricao?.toLowerCase().includes(termoPesquisa) || false
      const clubeMatch = produto.clube?.toLowerCase().includes(termoPesquisa) || false
      const ligaMatch = produto.liga?.toLowerCase().includes(termoPesquisa) || false
      const temporadaMatch = produto.temporada?.toLowerCase().includes(termoPesquisa) || false
      const corMatch = produto.cor?.toLowerCase().includes(termoPesquisa) || false
      const marcaMatch = produto.marca?.toLowerCase().includes(termoPesquisa) || false
      const subcategoriaMatch = produto.subcategoria?.toLowerCase().includes(termoPesquisa) || false
      const tagsMatch = produto.tags?.some(tag => tag.toLowerCase().includes(termoPesquisa)) || false
      
      return nomeMatch || descricaoMatch || clubeMatch || ligaMatch || temporadaMatch || corMatch || marcaMatch || subcategoriaMatch || tagsMatch
    })
  }

    // Filtrar por categoria
if (categoria) {
  if (categoria === "clubes") {
    produtosFiltrados = produtosFiltrados.filter(
      (p) => Array.isArray(p.categoria) 
        ? p.categoria.includes("clubes") 
        : p.categoria === "clubes"
    );
  } else if (categoria === "retro") {
    produtosFiltrados = produtosFiltrados.filter(
      (p) => Array.isArray(p.categoria) 
        ? p.categoria.includes("retro") 
        : p.categoria === "retro" || p.subcategoria === "retro"
    );
  } else if (categoria === "selecoes") {
    // Incluir também produtos de criança que pertençam às seleções nacionais
    produtosFiltrados = produtosFiltrados.filter((p) => {
      const isSelecoesCategoria = Array.isArray(p.categoria)
        ? p.categoria.includes("selecoes")
        : p.categoria === "selecoes";
      const isLigaSelecoes = p.liga === "selecoes-nacionais";
      const isCriancaSelecoes = (p.categoria === "crianca" || (Array.isArray(p.categoria) && p.categoria.includes("crianca"))) && isLigaSelecoes;
      return isSelecoesCategoria || isLigaSelecoes || isCriancaSelecoes;
    });
  } else if (categoria === "sneakers") {
    produtosFiltrados = produtosFiltrados.filter(
      (p) => p.subcategoria === "sneakers"
    );
  } else if (categoria === "dunk-low") {
    produtosFiltrados = produtosFiltrados.filter(
      (p) => p.categoria === "dunk-low"
    );
  } else {
    produtosFiltrados = produtosFiltrados.filter(
      (p) => Array.isArray(p.categoria) 
        ? p.categoria.includes(categoria) 
        : p.categoria === categoria
    );
  }
}

// Filtrar por clube
if (clube) {
  produtosFiltrados = produtosFiltrados.filter((p) => p.clube?.toLowerCase() === clube.toLowerCase());
}
  
    // Filtrar por cor
    if (cor) {
      produtosFiltrados = produtosFiltrados.filter((p) => p.cor === cor);
    }
  
    // Filtrar por liga
    if (liga) {
      if (liga === "outras-ligas") {
        const ligasPrincipais = [
          "liga-portuguesa",
          "premier-league",
          "la-liga",
          "serie-a",
          "bundesliga",
          "ligue-1",
        ];
        const clubesPortugueses = [
          "sporting",
          "benfica", 
          "porto",
          "sc-braga",
          "vitoria-sc",
          "braga"
        ];
        produtosFiltrados = produtosFiltrados.filter(
          (p) => p.liga && 
                 !ligasPrincipais.includes(p.liga) && 
                 !clubesPortugueses.includes(p.clube?.toLowerCase() || "")
        );
      } else {
        produtosFiltrados = produtosFiltrados.filter((p) => p.liga === liga);
      }
    }
  
    // Filtrar por temporada
    if (temporada) {
      produtosFiltrados = produtosFiltrados.filter((p) => p.temporada === temporada);
    }
  
    // Filtrar por edição especial
    if (edicao_especial !== undefined) {
      produtosFiltrados = produtosFiltrados.filter(
        (p) => p.edicao_especial === edicao_especial,
      );
    }
  
    // Filtrar por versão (jogador ou adepto)
    if (versao) {
      if (versao === "jogador") {
        // Mostrar apenas produtos marcados como versão jogador
        produtosFiltrados = produtosFiltrados.filter((p) => p.versao === "jogador");
      } else if (versao === "adepto") {
        // Mostrar apenas produtos sem versao ou marcados como adepto (padrão)
        produtosFiltrados = produtosFiltrados.filter(
          (p) => !p.versao || p.versao === "adepto"
        );
      }
    }
  
    // Filtrar por intervalo de preço
    if (precoMin) {
      produtosFiltrados = produtosFiltrados.filter((p) => p.preco >= Number(precoMin));
    }
    if (precoMax) {
      produtosFiltrados = produtosFiltrados.filter((p) => p.preco <= Number(precoMax));
    }

  // Excluir produtos ténis (exceto Benfica) apenas da página principal do catálogo
  // Não excluir se estivermos a filtrar por categorias específicas de sneakers
  if (!categoria || (categoria !== "sneakers" && categoria !== "airforce" && categoria !== "retro")) {
    produtosFiltrados = produtosFiltrados.filter((produto) => {
      // Manter produtos do Benfica mesmo que sejam sneakers
      if (produto.clube === "benfica") {
        return true
      }
      // Excluir produtos com subcategoria "sneakers" ou categoria "airforce" apenas do catálogo principal
      return produto.subcategoria !== "sneakers" && produto.categoria !== "airforce"
    })
  }

  // Função auxiliar para verificar se é temporada nova
  const isNovaTemporada = (temporada?: string) => {
    if (!temporada) return false
    return temporada === "2025/26" || 
           temporada === "25/26" || 
           temporada === "2025-2026" || 
           temporada.includes("2025") || 
           temporada.includes("25/26")
  }

  // Função auxiliar para verificar se é categoria de clubes
  const isCategoriaClubes = categoria === "clubes" || clube !== undefined

  // Aplicar ordenação
  switch (ordenar) {
    case "preco-asc":
      produtosFiltrados.sort((a, b) => {
        // Primeiro: priorizar temporadas novas
        const aNovaTemporada = isNovaTemporada(a.temporada)
        const bNovaTemporada = isNovaTemporada(b.temporada)
        
        if (aNovaTemporada && !bNovaTemporada) return -1
        if (!aNovaTemporada && bNovaTemporada) return 1
        
        // Dentro do mesmo grupo, ordenar por preço
        return a.preco - b.preco
      })
      break
    case "preco-desc":
      produtosFiltrados.sort((a, b) => {
        // Primeiro: priorizar temporadas novas
        const aNovaTemporada = isNovaTemporada(a.temporada)
        const bNovaTemporada = isNovaTemporada(b.temporada)
        
        if (aNovaTemporada && !bNovaTemporada) return -1
        if (!aNovaTemporada && bNovaTemporada) return 1
        
        // Dentro do mesmo grupo, ordenar por preço (desc)
        return b.preco - a.preco
      })
      break
    case "populares":
      produtosFiltrados.sort((a, b) => {
        // Primeiro: priorizar temporadas novas
        const aNovaTemporada = isNovaTemporada(a.temporada)
        const bNovaTemporada = isNovaTemporada(b.temporada)
        
        if (aNovaTemporada && !bNovaTemporada) return -1
        if (!aNovaTemporada && bNovaTemporada) return 1
        
        // Dentro do mesmo grupo, ordenar por popularidade
        return (b.numAvaliacoes || 0) - (a.numAvaliacoes || 0)
      })
      break
    case "avaliacao":
      produtosFiltrados.sort((a, b) => {
        // Primeiro: priorizar temporadas novas
        const aNovaTemporada = isNovaTemporada(a.temporada)
        const bNovaTemporada = isNovaTemporada(b.temporada)
        
        if (aNovaTemporada && !bNovaTemporada) return -1
        if (!aNovaTemporada && bNovaTemporada) return 1
        
        // Dentro do mesmo grupo, ordenar por avaliação
        return (b.avaliacao || 0) - (a.avaliacao || 0)
      })
      break
      case "recentes":
        default:
          // Ordenação especial para Barcelona
          if (clube === "barcelona") {
            produtosFiltrados.sort((a, b) => {
              // Primeiro: Produtos com "Special Edition" ou "Edição Especial" ou "Edicao Especial" no nome
              const aIsSpecial = a.nome?.toLowerCase().includes("special edition") || 
                                a.nome?.toLowerCase().includes("edição especial") || 
                                a.nome?.toLowerCase().includes("edicao especial")
              const bIsSpecial = b.nome?.toLowerCase().includes("special edition") || 
                                b.nome?.toLowerCase().includes("edição especial") || 
                                b.nome?.toLowerCase().includes("edicao especial")
              
              if (aIsSpecial && !bIsSpecial) return -1
              if (!aIsSpecial && bIsSpecial) return 1
              
              // Dentro de cada grupo, priorizar temporadas novas
              if (aIsSpecial === bIsSpecial) {
                const aNovaTemporada = isNovaTemporada(a.temporada)
                const bNovaTemporada = isNovaTemporada(b.temporada)
                
                if (aNovaTemporada && !bNovaTemporada) return -1
                if (!aNovaTemporada && bNovaTemporada) return 1
              }
              
              return 0
            })
          } else if (categoria === "retro") {
            // Se for categoria retro, priorizar os novos produtos do Benfica
            produtosFiltrados.sort((a, b) => {
              // Primeiro: priorizar temporadas novas
              const aNovaTemporada = isNovaTemporada(a.temporada)
              const bNovaTemporada = isNovaTemporada(b.temporada)
              
              if (aNovaTemporada && !bNovaTemporada) return -1
              if (!aNovaTemporada && bNovaTemporada) return 1
              
              // Produtos novos do Benfica retro têm prioridade máxima
              const produtosNovos = ["calcas-retro-slbenfica-adidas-originals", "fato-treino-slbenfica-adidas-originals", "sneakers-slbenfica-adidas-samba", "benfica-retro-adidas-originals-2526", "casaco-retro-benfica-adidas-originals-2526"]
              const aIsNovo = produtosNovos.includes(a.id)
              const bIsNovo = produtosNovos.includes(b.id)
              
              if (aIsNovo && !bIsNovo) return -1
              if (!aIsNovo && bIsNovo) return 1
              
              // Se ambos são novos, manter ordem: calças primeiro, depois fato de treino
              if (aIsNovo && bIsNovo) {
                if (a.id === "calcas-retro-slbenfica-adidas-originals") return -1
                if (b.id === "calcas-retro-slbenfica-adidas-originals") return 1
                return 0
              }
              
              // Para outros produtos retro, manter ordem original
              return 0
            })
          } else {
            // Definir prioridade dos maiores clubes da Europa
            const topEuropeanClubs = [
              "barcelona",
              "real-madrid",
              "manchester-united",
              "liverpool",
              "manchester-city",
              "psg",
              "bayern-munich",
              "arsenal",
              "chelsea",
              "milan",
              "inter",
              "juventus",
              "tottenham",
              "atletico-madrid",
              "borussia-dortmund",
              "napoli",
              "roma"
            ]
            
            // Função para verificar se um produto é de um top clube
            const isTopClub = (produto: Product) => {
              const clubeLower = produto.clube?.toLowerCase()
              return clubeLower && topEuropeanClubs.includes(clubeLower)
            }
            
            // Separar produtos em grupos para depois misturar
            // Apenas aplicar se não houver filtro de clube específico
            if (!clube) {
              const produtos2526TopClubs: Product[] = []
              const produtos2526Outros: Product[] = []
              const produtosOutrosTopClubs: Product[] = []
              const produtosOutros: Product[] = []
              const produtosBody: Product[] = []
              
              produtosFiltrados.forEach(produto => {
                // Separar produtos "body" para o final
                const isBody = produto.categoria === "body"
                
                if (isBody) {
                  produtosBody.push(produto)
                  return
                }
                
                const is2526 = isNovaTemporada(produto.temporada)
                const isTop = isTopClub(produto)
                
                if (is2526 && isTop) {
                  produtos2526TopClubs.push(produto)
                } else if (is2526 && !isTop) {
                  produtos2526Outros.push(produto)
                } else if (!is2526 && isTop) {
                  produtosOutrosTopClubs.push(produto)
                } else {
                  produtosOutros.push(produto)
                }
              })
              
              // Função para baralhar array mantendo alguma aleatoriedade baseada no ID
              const shuffleArray = (array: Product[]) => {
                const shuffled = [...array]
                // Usar um hash simples baseado no ID para criar uma ordem pseudo-aleatória mas consistente
                shuffled.sort((a, b) => {
                  const hashA = a.id.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)
                  const hashB = b.id.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)
                  return (hashA % 1000) - (hashB % 1000)
                })
                return shuffled
              }
              
              // Baralhar os grupos de top clubs
              const produtos2526TopClubsShuffled = shuffleArray(produtos2526TopClubs)
              const produtosOutrosTopClubsShuffled = shuffleArray(produtosOutrosTopClubs)
              
              // Reconstruir array na ordem: 2025/26 top clubs (baralhados), 2025/26 outros, outros top clubs (baralhados), outros, body (no final)
              produtosFiltrados = [
                ...produtos2526TopClubsShuffled,
                ...produtos2526Outros,
                ...produtosOutrosTopClubsShuffled,
                ...produtosOutros,
                ...produtosBody
              ]
            } else {
              // Se houver filtro de clube, separar body para o final e ordenar resto por temporada 2025/26
              const produtosBody: Product[] = []
              const produtosOutros: Product[] = []
              
              produtosFiltrados.forEach(produto => {
                if (produto.categoria === "body") {
                  produtosBody.push(produto)
                } else {
                  produtosOutros.push(produto)
                }
              })
              
              // Ordenar produtos não-body por temporada nova
              produtosOutros.sort((a, b) => {
              const aIs2526 = isNovaTemporada(a.temporada)
              const bIs2526 = isNovaTemporada(b.temporada)
              
              if (aIs2526 && !bIs2526) return -1
              if (!aIs2526 && bIs2526) return 1
              
              return 0
            })
              
              // Reconstruir: produtos não-body primeiro, body no final
              produtosFiltrados = [...produtosOutros, ...produtosBody]
            }
          }
          break
  }

  // ORDENAÇÃO FINAL: BODYS E KITS DE CRIANÇA
  // Sempre colocar BODYS por último (em todas as categorias)
  // Nas categorias de CLUBES, colocar KITS DE CRIANÇA em antepenúltimo
  const produtosNormais: Product[] = []
  const produtosCrianca: Product[] = []
  const produtosBody: Product[] = []
  
  produtosFiltrados.forEach(produto => {
    // Identificar se é body
    const isBody = produto.categoria === "body" || 
                   (typeof produto.categoria === "string" && produto.categoria.includes("body")) ||
                   (Array.isArray(produto.categoria) && produto.categoria.includes("body"))
    
    // Identificar se é kit de criança (apenas em categorias de clubes)
    const isCrianca = isCategoriaClubes && (
      produto.categoria === "crianca" || 
      (typeof produto.categoria === "string" && produto.categoria.includes("crianca")) ||
      (Array.isArray(produto.categoria) && produto.categoria.includes("crianca"))
    )
    
    if (isBody) {
      produtosBody.push(produto)
    } else if (isCrianca) {
      produtosCrianca.push(produto)
    } else {
      produtosNormais.push(produto)
    }
  })
  
  // Reconstruir array na ordem final:
  // Categorias de CLUBES: produtos normais, kits de criança (antepenúltimo), bodys (último)
  // Outras categorias: produtos normais, bodys (último)
  if (isCategoriaClubes) {
    produtosFiltrados = [...produtosNormais, ...produtosCrianca, ...produtosBody]
  } else {
    produtosFiltrados = [...produtosNormais, ...produtosBody]
  }

  // Armazenar no cache antes de retornar
  setCachedData(cacheKey, produtosFiltrados)

  return produtosFiltrados
}

// Função para obter produtos em destaque
export async function getDestaques() {
  // Simular um atraso de rede
  await new Promise((resolve) => setTimeout(resolve, 50))

  // Filtrar produtos em destaque
  const produtosDestaque = produtos.filter((p) => p.destaque)

  // Separar as camisolas do Real Madrid 2025/26
  const realMadridCamisolas = produtosDestaque.filter(
    (p) => p.id === "real-madrid-25-26-home" || p.id === "real-madrid-25-26-away"
  )

  // Separar a camisola do Sporting 2025/26
  const sportingCamisola = produtosDestaque.find((p) => p.id === "21")

  // Filtrar os outros produtos
  const outrosProdutos = produtosDestaque.filter(
    (p) => 
      p.id !== "21" && 
      p.id !== "real-madrid-25-26-home" && 
      p.id !== "real-madrid-25-26-away"
  )

  // Ordenar os produtos: Sporting, Real Madrid, e depois os outros
  const ordenedProducts = [
    ...(sportingCamisola ? [sportingCamisola] : []),
    ...realMadridCamisolas,
    ...outrosProdutos
  ]

  // Retornar todos os produtos em destaque (removida limitação de 8)
  return ordenedProducts
}

// Função para criar slug a partir do nome do produto
function criarSlug(nome: string): string {
  return nome
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '') // Remove acentos
    .replace(/[^a-z0-9\s-]/g, '') // Remove caracteres especiais
    .replace(/\s+/g, '-') // Substitui espaços por hífens
    .replace(/-+/g, '-') // Remove hífens duplicados
    .replace(/^-+|-+$/g, '') // Remove hífens do início e fim
    .trim()
}

// Função para obter um produto pelo ID (aceita tanto ID numérico quanto slug)
export async function getProdutoById(id: string) {
  // Simular um atraso de rede
  await new Promise((resolve) => setTimeout(resolve, 50))

  // Primeiro tentar encontrar por ID numérico
  let produto = produtos.find((p) => p.id === id)
  
  // Se não encontrar, tentar encontrar por slug (nome do produto)
  if (!produto) {
    produto = produtos.find((p) => criarSlug(p.nome) === id)
  }

  return produto
}

// Função para obter produtos relacionados
export async function getProdutosRelacionados(id: string, categoria: string) {
  // Simular um atraso de rede
  await new Promise((resolve) => setTimeout(resolve, 50))

  const produtosRelacionados = produtos
    .filter((p) => p.id !== id && p.categoria === categoria)

  // Ordenação determinística baseada no ID para evitar problemas de hidratação
  // Usar uma hash simples do ID para criar uma ordenação "pseudo-aleatória" mas consistente
  const hashString = (str: string) => {
    let hash = 0
    for (let i = 0; i < str.length; i++) {
      const char = str.charCodeAt(i)
      hash = ((hash << 5) - hash) + char
      hash = hash & hash // Convert to 32-bit integer
    }
    return hash
  }

  return produtosRelacionados
    .sort((a, b) => {
      // Criar uma ordenação baseada na combinação do ID atual com os IDs dos produtos
      const hashA = hashString(id + a.id)
      const hashB = hashString(id + b.id)
      return hashA - hashB
    })
    .slice(0, 4)
}

// Função para obter todos os produtos (usado para sitemap)
export async function getAllProducts() {
  // Usar cache para evitar processamento desnecessário
  const cacheKey = CACHE_KEYS.PRODUTOS_FILTERED(JSON.stringify({}))
  
  // Verificar cache primeiro
  const cached = getCachedData<Product[]>(cacheKey)
  if (cached) {
    return cached
  }
  
  // Simular um atraso de rede
  await new Promise((resolve) => setTimeout(resolve, 50))
  
  // Retornar todos os produtos
  const todosProdutos = [...produtos]
  
  // Armazenar no cache
  setCachedData(cacheKey, todosProdutos)
  
  return todosProdutos
}
